onClick:
{{
(async () => {
  if (
    !input_login.isValid   ||
    !inp_nome.isValid      ||
    !inp_email.isValid     ||
    !inp_sobrenome.isValid ||
    !inp_senha.isValid     ||
    !sel_filial.isValid
  ) {
    showAlert("Preencha todos os campos obrigatórios.", "error");
    return;
  }

  try {
    await insert_usuario.run();
    showAlert("Usuário salvo com sucesso!", "success");
    resetWidget("input_login", true);
    resetWidget("inp_nome", true);
    resetWidget("inp_sobrenome", true);
    resetWidget("inp_email", true);
    resetWidget("inp_senha", true);
    resetWidget("sel_filial", true);
    closeModal("ModalCadastroUsuario");
  } catch (e) {
    const msg =
      e?.responseMeta?.error?.message ||
      e?.message ||
      "veja os Logs da query";
    showAlert("Erro ao salvar: " + msg, "error");
  }
})()
}}
